script:
start:
  await/setup:
    form:
      elements:
        text/prompt_label:
          label: Label:
          required@bool: yes
  
        sheet/prompt_syntax:
          label: Mode:
          required@bool: yes
          data@json:
            [
              {
                "mode": "cerb_query",
                "name": "Cerb Query"
              },
              {
                "mode": "html",
                "name": "HTML"
              },
              {
                "mode": "json",
                "name": "JSON"
              },
              {
                "mode": "markdown",
                "name": "Markdown"
              },
              {
                "mode": "text",
                "name": "Text"
              },
              {
                "mode": "yaml",
                "name": "YAML"
              }
            ]
          default@list:
            cerb_query
          schema:
            layout:
              headings@bool: no
              paging@bool: no
            columns:
              selection/mode:
                params:
                  mode: single
              text/name:
                params:
                  bold@bool: yes
  await/default:
    form:
      elements:
        editor/prompt_default:
          label: Default:
          mode: {{prompt_mode}}

  return:
    snippet@text:
      editor/prompt_{{random_string(6)|lower}}:
        label: {{prompt_label}}
        syntax: {{prompt_syntax}}
        {% if prompt_default -%}
        default@text:
          {{prompt_default|indent('    ', 2)}}
        {% endif -%}
