script:
start:
  await/field:
    form:
      elements:
        say/type:
          content: ## Yield: Text
          
        text/prompt_label:
          label: Label:
          required@bool: yes
          
        sheet/prompt_type:
          label: Type:
          required@bool: yes
          data@json:
            [
              {
                "key": "freeform",
                "name": "Freeform",
                "description": "An arbitrary string of text"
              },
              {
                "key": "bool",
                "name": "Boolean",
                "description": "true or false"
              },
              {
                "key": "date",
                "name": "Date",
                "description": "tomorrow 5pm, +2 weeks, Friday"
              },
              {
                "key": "decimal",
                "name": "Decimal",
                "description": "3.1415"
              },
              {
                "key": "email",
                "name": "Email",
                "description": "client@example.com"
              },
              {
                "key": "geopoint",
                "name": "Geopoint",
                "description": "44.226737, 20.7953 (lat, long)"
              },
              {
                "key": "ip",
                "name": "IP",
                "description": "1.2.3.4 or a1b2:c3d4:e5f6:a1b2:c3d4:e5f6:a1b2:c3d4"
              },
              {
                "key": "IPv4",
                "name": "IPv4",
                "description": "1.2.3.4"
              },
              {
                "key": "IPv6",
                "name": "IPv6",
                "description": "a1b2:c3d4:e5f6:a1b2:c3d4:e5f6:a1b2:c3d4"
              },
              {
                "key": "number",
                "name": "Number",
                "description": "1234"
              },
              {
                "key": "record_type",
                "name": "Record Type",
                "description": "address, comment, ticket, worker"
              },
              {
                "key": "timestamp",
                "name": "Timestamp",
                "description": "1601337600"
              },
              {
                "key": "uri",
                "name": "URI",
                "description": "alphanumeric_with_underscores (a-z, 0-9, _)"
              },
              {
                "key": "url",
                "name": "URL",
                "description": "https://example.com/path/file.png"
              }
            ]
          default: freeform
          schema:
            layout:
              headings@bool: no
              paging@bool: no
            columns:
              selection/key:
                params:
                  mode: single
              text/name:
                params:
                  bold@bool: yes
              text/description:
              
        sheet/prompt_required:
          label: Required:
          required@bool: yes
          data@json:
            [
              { "name": "yes" },
              { "name": "no" }
            ]
          limit: 10
          default: yes
          schema:
            layout:
              headings@bool: no
            columns:
              selection/_selection:
                params:
                  mode: single
                  value_key: name
              text/name:
              
  await/params:
    form:
      elements:
        say/type:
          content@text:
            ## Yield: Text ({{prompt_type}})
            
        text/prompt_default:
          label: Default:
          default@text:
            {% if 'bool' == prompt_type -%}
            true
            {% endif -%}
            
        text/prompt_placeholder:
          label: Placeholder:
          default@text:
            {% if 'bool' == prompt_type -%}
            true or false
            {% endif -%}
                
        text/prompt_max_length:
          label: Max Length:
          type: number
          hidden@bool:
            {% if 'bool' == prompt_type -%}
            yes
            {% endif -%}
            
  return:
    snippet@text:
      text/prompt_{{random_string(6)|lower}}:
        label: {{prompt_label}}
        required@bool: {{prompt_required == true ? 'yes'}}
        type: {{prompt_type}}
        {% if prompt_default -%}
        default@text:
          {{prompt_default|indent('    ',2)}}
        {% endif -%}
        {% if prompt_placeholder -%}
        placeholder: {{prompt_placeholder}}
        {% endif -%}
        {% if prompt_max_length -%}
        max_length: {{prompt_max_length}}
        {% endif -%}
