script:
start:
  data.query:
    output: results
    inputs:
      query@text:
        type: record.types
        {% if sheet_filter -%}
        filter: {{sheet_filter}}
        {% endif -%}
        {% if sheet_limit -%}
        limit: {{sheet_limit}}
        {% endif -%}
        {% if sheet_page -%}
        page: {{sheet_page}}
        {% endif -%}
        format: dictionaries
    on_success:
      return:
        data@key: results:data
        total@key: results:_:paging:page:rows:of
    on_error:
      error:
        message@key: results:error
