script:
inputs:
  text/record_type:
    type: record_type
    required@bool: yes
  text/query_required:
    type: freeform

start:
  data.query:
    output: results
    inputs:
      query@text:
        type: worklist.records
        of: {{inputs.record_type}}
        query.required: (
          {{inputs.query_required}}
          {% if sheet_limit %}limit: {{sheet_limit}}{% endif %}
        )
        {% if sheet_filter -%}
        query: (
          {{sheet_filter}}
        )
        {% endif -%}
        {% if sheet_limit and sheet_page -%}
        page: {{sheet_page}}
        {% endif -%}
        format: dictionaries
    on_success:
      return:
        data@key: results:data
        total@key: results:_:paging:page:rows:of
    on_error:
      error:
        message@key: results:error
