script:
start:
  data.query:
    output: results
    inputs:
      query@text:
        type:ui.icons
        {% if sheet_limit -%}
        limit: {{sheet_limit}}
        page: {{sheet_page}}
        {% endif -%}
        {% if sheet_filter -%}
        filter: {{sheet_filter}}
        {% endif -%}
        format:dictionaries
    on_success:
      return:
        data@key: results:data
        total@key: results:_:paging:page:rows:of
