extension_params.template:
{% if not cerb_record_writeable(record__context, record_id) %}
	<div style="color:var(--cerb-color-background-contrast-125);text-align:center;font-size:1.2em;">
		(you do not have permission to edit this record)
	</div>
	
{% else %}
	{% set is_closed = 'closed' == record_status %}
	{% set is_deleted = 'deleted' == record_status %}
	
	<div id="widget{{widget_id}}" style="padding:0px 5px 5px 5px;">
		{% if is_closed or is_deleted %}
		<button type="button" data-shortcut="reopen">
			<span class="glyphicons glyphicons-upload"></span> {{'common.reopen'|cerb_translate|capitalize}}
		</button>
		{% endif %}
		
		{% if not is_deleted and not is_closed and cerb_has_priv('contexts.cerberusweb.contexts.ticket.update') %}
			<button type="button" data-shortcut="close" title="(C)">
			<span class="glyphicons glyphicons-circle-ok"></span> {{'common.close'|cerb_translate|capitalize}}
		</button>
		{% endif %}
		
		{% if record_spam_training is empty and not is_deleted and cerb_has_priv('contexts.cerberusweb.contexts.ticket.update') %}
		<button type="button" data-shortcut="spam" title="(S)">
			<span class="glyphicons glyphicons-ban"></span> {{'common.spam'|cerb_translate|capitalize}}
		</button>
		{% endif %}

		{% if not is_deleted and cerb_has_priv('contexts.cerberusweb.contexts.ticket.delete') %}
		<button type="button" data-shortcut="delete" title="(X)">
			<span class="glyphicons glyphicons-circle-remove"></span> {{'common.delete'|cerb_translate|capitalize}}
		</button>
		{% endif %}
		
		{%if cerb_has_priv('contexts.cerberusweb.contexts.ticket.merge') %}
		<button type="button" data-shortcut="merge">
			<span class="glyphicons glyphicons-git-merge"></span> {{'common.merge'|cerb_translate|capitalize}}
		</button>
		{% endif %}
		
		<button type="button" data-shortcut="read-all" title="(A)">
			<span class="glyphicons glyphicons-book-open"></span> {{'display.button.read_all'|cerb_translate|capitalize}}
		</button>
	</div>
	
	<script type="text/javascript">
	$(function() {
		var $widget = $('#widget{{widget_id}}');
		var $parent = $widget.closest('.cerb-profile-widget')
			.off('.widget{{widget_id}}')
			;
		var $tab = $parent.closest('.cerb-profile-layout');
		
		$widget.find('button.cerb-peek-editor')
			.cerbPeekTrigger()
			.on('cerb-peek-saved', function(e) {
					e.stopPropagation();
					document.location.reload();
			})
			;
			
		var $button_reopen = $widget.find('button[data-shortcut="reopen"]')
			.on('click', function(e) {
				e.stopPropagation();
				e.preventDefault();
				
				var formData = new FormData();
				formData.set('c', 'profiles');
				formData.set('a', 'invoke');
				formData.set('module', 'ticket');
				formData.set('action', 'quickStatus');
				formData.set('ticket_id', "{{record_id}}");
				formData.set('status', 'o');

				genericAjaxPost(formData, null, null, function(event) {
						document.location.reload();
				});				
			})
			;
		
		var $button_close = $widget.find('button[data-shortcut="close"]')
			.on('click', function(e) {
				e.stopPropagation();
				e.preventDefault();
				
				var formData = new FormData();
				formData.set('c', 'profiles');
				formData.set('a', 'invoke');
				formData.set('module', 'ticket');
				formData.set('action', 'quickStatus');
				formData.set('ticket_id', "{{record_id}}");
				formData.set('status', 'c');

				genericAjaxPost(formData, null, null, function(event) {
						document.location.reload();
				});
			})
			;
		
		var $button_delete = $widget.find('button[data-shortcut="delete"]')
			.on('click', function(e) {
				e.stopPropagation();
				e.preventDefault();
				
				var formData = new FormData();
				formData.set('c', 'profiles');
				formData.set('a', 'invoke');
				formData.set('module', 'ticket');
				formData.set('action', 'quickStatus');
				formData.set('ticket_id', "{{record_id}}");
				formData.set('status', 'd');

				genericAjaxPost(formData, null, null, function(event) {
						document.location.reload();
				});
			});
		
		var $button_spam = $widget.find('button[data-shortcut="spam"]')
			.on('click', function(e) {
				e.stopPropagation();
				e.preventDefault();
				
				var formData = new FormData();
				formData.set('c', 'profiles');
				formData.set('a', 'invoke');
				formData.set('module', 'ticket');
				formData.set('action', 'quickSpam');
				formData.set('ticket_id', "{{record_id}}");
				formData.set('is_spam', '1');

				genericAjaxPost(formData, null, null, function(event) {
						document.location.reload();
				});				
			})
			;
		
		var $button_merge = $widget.find('button[data-shortcut="merge"]');
		var $button_readall = $widget.find('button[data-shortcut="read-all"]');
	
		{% if cerb_has_priv('contexts.cerberusweb.contexts.ticket.merge') %}
		$button_merge
			.on('click', function(e) {
				var $merge_popup = genericAjaxPopup('peek','c=internal&a=invoke&module=records&action=renderMergePopup&context={{record__context}}&ids={{record_id}}',null,false,'50%');
				
				$merge_popup.on('record_merged', function(e) {
					e.stopPropagation();
					document.location.reload();
				});
			})
			;
		{% endif %}
		
		$button_readall.on('click', function(e) {
			var evt = $.Event('cerb-widget-refresh');
			evt.widget_id = {{{uid.profile_widget_convo}}};
			evt.refresh_options = {'expand_all': 1};
			$tab.triggerHandler(evt);
		});
		
		$parent.on('keydown.widget{{widget_id}}', null, 'A', function(e) {
			e.stopPropagation();
			e.preventDefault();
			$button_readall.click();
		});

		$parent.on('keydown.widget{{widget_id}}', null, 'C', function(e) {
			e.stopPropagation();
			e.preventDefault();
			$button_close.click();
		});
		
		$parent.on('keydown.widget{{widget_id}}', null, 'S', function(e) {
			e.stopPropagation();
			e.preventDefault();
			$button_spam.click();
		});
		
		$parent.on('keydown.widget{{widget_id}}', null, 'X', function(e) {
			e.stopPropagation();
			e.preventDefault();
			$button_delete.click();
		});

		$parent.on('keydown.widget{{widget_id}}', null, 'H', function(e) {
			e.stopPropagation();
			e.preventDefault();
			
			$tab
				.find('.cerb-profile-widget--link:contains(Ticket)')
				.closest('.cerb-profile-widget')
				.find('button.cerb-search-trigger:contains(Participant History)')
				.click()
				;
		});
	});
	</script>
{% endif %}

{% set is_writeable = cerb_record_writeable(record__context,record_id,current_worker__context,current_worker_id) %}
<div id="widget{{widget_id}}">
	<div style="float:left;padding:0 10px 5px 5px;">
		<img src="{{cerb_avatar_url(record_owner__context,record_owner_id,record_owner_updated)}}" width="50" style="border-radius:50px;">
	</div>
	<div style="position:relative;">
		<div>
			{% if record_owner_id == 0 %}
				<span style="font-size:2em;color:var(--cerb-color-background-contrast-100);font-weight:bold;">
					({{'common.nobody'|cerb_translate|lower}})
				</span>
			{% else %}
					<a href="javascript:;" class="cerb-peek-trigger no-underline" style="font-size:2em;var(--cerb-color-background-contrast-100);font-weight:bold;" data-context="cerberusweb.contexts.worker" data-context-id="{{record_owner_id}}">{{record_owner__label}}</a>
					<div>
						{{record_owner_title}}
					</div>
			{% endif %}

			{% if is_writeable %}
			<div class="cerb-buttons-toolbar" style="display:none;position:absolute;top:0;right:0;">
				<button type="button" title="{{'common.assign'|cerb_translate|capitalize}} (Shift+T)" class="cerb-button-assign cerb-chooser-trigger" data-context="cerberusweb.contexts.worker" data-single="true" data-query="group:(id:{{record_group_id}}) isDisabled:n">
					<span class="glyphicons glyphicons-send"></span>
				</button>
				
				{% if record_owner_id %}
				<button type="button" title="{{'common.unassign'|cerb_translate|capitalize}} (U)" class="cerb-button-unassign">
					<span class="glyphicons glyphicons-circle-remove"></span>
				</button>
				{% endif %}
			</div>
			{% endif %}
		</div>
	</div>
</div>

<script type="text/javascript">
$(function() {
	var $widget = $('#widget{{widget_id}}');
	var $parent = $widget.closest('.cerb-profile-widget')
		.off('.widget{{widget_id}}')
		;
	var $toolbar = $widget.find('div.cerb-buttons-toolbar');
	var $tab = $parent.closest('.cerb-profile-layout');
	
	var $button_take = $widget.find('button.cerb-button-take');
	var $button_assign = $widget.find('.cerb-chooser-trigger');
	var $button_unassign = $widget.find('button.cerb-button-unassign');

	$widget.find('.cerb-peek-trigger')
		.cerbPeekTrigger()
		;
		
	{% if is_writeable %}
	$widget
		.on('mouseover', function() {
			$toolbar.show();
		})
		.on('mouseout', function() {
			$toolbar.hide();
		})
		;
	{% endif %}
	
	{% if is_writeable %}
	$button_assign
		.cerbChooserTrigger()
		.on('cerb-chooser-selected', function(e) {
			if(!e.values || !Array.isArray(e.values))
				return;
				
			if(e.values.length != 1)
				return;

			var owner_id = e.values[0];
			
			var formData = new FormData();
			formData.set('c', 'profiles');
			formData.set('a', 'invoke');
			formData.set('module', 'ticket');
			formData.set('action', 'quickAssign');
			formData.set('ticket_id', '{{record_id}}');
			formData.set('owner_id', owner_id);
			
			genericAjaxPost(formData, null, null, function(response) {
				// Refresh the entire page
				document.location.reload();
			});
		})
		;
		
	$button_unassign
		.on('click', function(e) {
			var formData = new FormData();
			formData.set('c', 'profiles');
			formData.set('a', 'invoke');
			formData.set('module', 'ticket');
			formData.set('action', 'quickAssign');
			formData.set('ticket_id', '{{record_id}}');
			formData.set('owner_id', '0');
			
			genericAjaxPost(formData, null, null, function(response) {
				// Refresh the entire page
				document.location.reload();
			});
		})
		;
		
		{# Allow the (t)ake shortcut for 'assign to me' if unassigned #}
		{% if record_owner_id == 0%}
		$parent.on('keydown.widget{{widget_id}}', null, 'T', function(e) {
			e.stopPropagation();
			e.preventDefault();
			
			var formData = new FormData();
			formData.set('c', 'profiles');
			formData.set('a', 'invoke');
			formData.set('module', 'ticket');
			formData.set('action', 'quickAssign');
			formData.set('ticket_id', '{{record_id}}');
			formData.set('owner_id', '{{current_worker_id}}');

			genericAjaxPost(formData, null, null, function(response) {
				// Refresh the entire page
				document.location.reload();
			});
		});
		{% endif %}
		
		$parent.on('keydown.widget{{widget_id}}', null, 'Shift+T', function(e) {
			e.stopPropagation();
			e.preventDefault();
			$button_assign.click();
		});

		$parent.on('keydown.widget{{widget_id}}', null, 'U', function(e) {
			e.stopPropagation();
			e.preventDefault();
			$button_unassign.click();
		});

	{% endif %}
});
</script>
